function processXML() {
  var xml = '<test>value</test>';
  if (xml.indexOf('<') !== -1) {
    console.log("Found XML tags: " + xml);
  }
  return xml.replace(/&/g, '&amp;').replace(/</g, '&lt;');
}

// Complex expressions
var result = (x < y) && (y > z) ? "true" : "false";
document.write("Result: " + result);
